import pandas as pd
import numpy as np
from numpy import dot
from numpy.linalg import norm

class connect_division_to_recommend():
    
    def __init__(self, ocr_result, ip):
        self.ip = ip
        self.cp = ip.c_p()
        self.cg = ip.c_g()
        self.ocr = ocr_result
        self.df = None
        self.t_g_o = None
        self.t_p_o = None
        self.i_diction = None
        
    def cos_sim(self, A, B):
        return dot(A, B)/(norm(A)*norm(B))
    
    def make_vec(self):
        sim = []
        for idx in range(len(self.df)):
            olive = self.df.iloc[idx][4]
            olive = olive.split(',')
            check_list = []

            # 벡터 필드 생성
            for i in self.cg:
                cnt = 0
                if i in self.t_g_o and i in olive: 
                    cnt = 1
                else:
                    if i in olive:
                        if i in self.ip.vec()[0]: #훌륭함
                            cnt = 0.9
                        elif i in self.ip.vec()[1]: #좋음
                            cnt = 0.8
                check_list.append(cnt)

            sim.append(self.cos_sim(check_list, self.i_diction))

        return sim
    
    def make_rank(self):
        
        rank = []

        for series in self.df['성분']:
            series = series.split(',')
            
        #     print(series)
            bad = 0 #(피부타입)=100,
            bad_count0 = 0 #주의(보통)=1
            bad_count1 = 0 #주의(나쁨)=2
            bad_count2 = 0 #주의(매우나쁨)=3 

            for ingredient in series: # top 5 전성분 파악
                ingredient = ingredient.strip()

                # 카운트 - 피부타입
                if ingredient in self.ip.rec()[3]:
                    bad += 100
                # 카운트 - 보통
                elif ingredient in self.ip.rec()[0]:
                    bad_count0 += 1
                # 카운트 - 나쁨  
                elif ingredient in self.ip.rec()[1]:
                    bad_count1 += 2
                # 카운트 - 매우나쁨    
                elif ingredient in self.ip.rec()[2]:
                    bad_count2 += 3

            rank.append(bad + bad_count0 + bad_count1 + bad_count2)
            
        return rank
    
    def base_check(self, object_name):
        # 사용자가 사용하는 제품속 사용자에게 좋은 성분
        self.t_g_o = [] # target_good_objects
        for i in self.ocr:
            if i in self.cg:
                self.t_g_o.append(i)
                
        self.t_p_o = [] # target_problems_objects
        for i in self.ocr:
            if i in self.cp:
                self.t_p_o.append(i)
                
        if len(self.t_p_o) > 0:
            # 유사도 기반 추천
#             return self.t_p_o
            return self.t_tpo(object_name)
        
        else:
            # 유사도 및 불순물 기반 추천
#             return self.t_g_o
            return self.non_tpo(object_name)
            
    def non_tpo(self, object_name):
        self.i_diction = list(1 for i in range(len(self.cg)))
        self.df = pd.read_csv("./data/{}.csv".format(object_name))
        self.df = self.df[['이름','성분','가격','img_src','href', '1ml당 가격']]
        
        self.df["유사도"] = self.make_vec()
        self.df = self.df.sort_values('유사도', ascending=False).reset_index(drop=True)
        self.df = self.df.head()
        self.df["불순물"] = self.make_rank()
        self.df = self.df.sort_values(by=['불순물', '유사도'], ascending=[True, False]).reset_index(drop=True)
        
        return self.df
    
    def t_tpo(self, object_name):
        self.i_diction = list(1 for i in range(len(self.cg)))
        self.df = pd.read_csv("./data/{}.csv".format(object_name))
        self.df = self.df[['이름','성분','가격','img_src','href', '1ml당 가격']]
        index = []
        
        for idx, x in self.df['성분'].items(): # 시리즈 받기
            x = x.split(",")
            for y in x: # 시리즈 속 리스트 받기
                if y in self.t_p_o:
                    index.append(idx)
                else:
                    pass

        for n in index: # t_p_o (사용자가 주의해야할 성분) 이 있는 index는 컬럼에서 아예 제외
            try:
                self.df = self.df.drop(index=n)
            except:
                pass

        self.df = self.df.reset_index(drop=True)
        self.df["유사도"] = self.make_vec()
        self.df = self.df.sort_values('유사도', ascending=False).reset_index(drop=True)
        self.df = self.df.head()
        
        return self.df